contracts/QuestionManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title QuestionManager
 * @dev Manages questions, answers, and engagement on the VeChain Q&A platform
 * @notice This contract handles the core Q&A functionality with anti-farming mechanisms
 */
contract QuestionManager is Ownable, ReentrancyGuard {

    // Structs
    struct Question {
        uint256 id;
        address asker;
        string title;
        string content;
        string[] tags;
        uint256 timestamp;
        uint256 bounty; // VET bounty offered by asker
        bool isResolved;
        address approvedAnswer; // Best answer approved by asker
        uint256 totalAnswers;
        uint256 totalUpvotes;
    }

    struct Answer {
        uint256 id;
        uint256 questionId;
        address author;
        string content;
        uint256 timestamp;
        uint256 upvotes;
        uint256 downvotes;
        bool isApproved;
    }

    // Separate mappings for upvoters and downvoters
    mapping(uint256 => mapping(address => bool)) public answerUpvoters;
    mapping(uint256 => mapping(address => bool)) public answerDownvoters;

    struct UserProfile {
        address user;
        uint256 questionsAsked;
        uint256 answersGiven;
        uint256 totalUpvotes;
        uint256 reputation;
        bool isVerified;
        uint256 lastActivity;
    }

    // State variables
    uint256 public questionCounter;
    uint256 public answerCounter;
    uint256 public totalBountyPool;

    // Mappings
    mapping(uint256 => Question) public questions;
    mapping(uint256 => Answer) public answers;
    mapping(address => UserProfile) public userProfiles;
    mapping(address => uint256[]) public userQuestions;
    mapping(address => uint256[]) public userAnswers;
    mapping(string => uint256[]) public questionsByTag;

    // Anti-farming mechanisms
    mapping(address => uint256) public lastQuestionTime;
    mapping(address => uint256) public lastAnswerTime;
    mapping(address => uint256) public dailyQuestions;
    mapping(address => uint256) public dailyAnswers;
    mapping(address => uint256) public lastDailyReset;

    uint256 public constant MIN_QUESTION_INTERVAL = 1 hours;
    uint256 public constant MIN_ANSWER_INTERVAL = 30 minutes;
    uint256 public constant MAX_DAILY_QUESTIONS = 10;
    uint256 public constant MAX_DAILY_ANSWERS = 50;
    uint256 public constant MIN_REPUTATION_TO_ASK = 10;

    // Events
    event QuestionAsked(
        uint256 indexed questionId,
        address indexed asker,
        string title,
        string[] tags,
        uint256 bounty
    );

    event AnswerPosted(
        uint256 indexed answerId,
        uint256 indexed questionId,
        address indexed author,
        string content
    );

    event AnswerUpvoted(
        uint256 indexed answerId,
        address indexed voter,
        address indexed author
    );

    event AnswerApproved(
        uint256 indexed questionId,
        uint256 indexed answerId,
        address indexed asker
    );

    event ReputationUpdated(
        address indexed user,
        uint256 newReputation,
        uint256 change
    );

    constructor() Ownable() {}

    /**
     * @dev Ask a new question with bounty
     * @param title Question title
     * @param content Question content
     * @param tags Array of tags
     * @param bounty VET bounty for best answer
     */
    function askQuestion(
        string memory title,
        string memory content,
        string[] memory tags,
        uint256 bounty
    ) external payable nonReentrant {
        require(bytes(title).length > 0, "Title cannot be empty");
        require(bytes(content).length > 0, "Content cannot be empty");
        require(tags.length > 0, "At least one tag required");
        require(msg.value >= bounty, "Insufficient bounty");
        require(
            userProfiles[msg.sender].reputation >= MIN_REPUTATION_TO_ASK,
            "Insufficient reputation to ask questions"
        );
        require(
            block.timestamp - lastQuestionTime[msg.sender] >= MIN_QUESTION_INTERVAL,
            "Too soon to ask another question"
        );
        require(
            dailyQuestions[msg.sender] < MAX_DAILY_QUESTIONS,
            "Daily question limit reached"
        );

        // Reset daily counters if new day
        if (block.timestamp - lastDailyReset[msg.sender] >= 1 days) {
            dailyQuestions[msg.sender] = 0;
            dailyAnswers[msg.sender] = 0;
            lastDailyReset[msg.sender] = block.timestamp;
        }

        questionCounter++;
        uint256 questionId = questionCounter;

        questions[questionId] = Question({
            id: questionId,
            asker: msg.sender,
            title: title,
            content: content,
            tags: tags,
            timestamp: block.timestamp,
            bounty: bounty,
            isResolved: false,
            approvedAnswer: address(0),
            totalAnswers: 0,
            totalUpvotes: 0
        });

        // Update user profile
        userProfiles[msg.sender].questionsAsked++;
        userProfiles[msg.sender].lastActivity = block.timestamp;
        userQuestions[msg.sender].push(questionId);

        // Update tag mappings
        for (uint256 i = 0; i < tags.length; i++) {
            questionsByTag[tags[i]].push(questionId);
        }

        // Update anti-farming tracking
        lastQuestionTime[msg.sender] = block.timestamp;
        dailyQuestions[msg.sender]++;
        totalBountyPool += bounty;

        emit QuestionAsked(questionId, msg.sender, title, tags, bounty);
    }

    /**
     * @dev Post an answer to a question
     * @param questionId ID of the question
     * @param content Answer content
     */
    function postAnswer(
        uint256 questionId,
        string memory content
    ) external nonReentrant {
        require(questions[questionId].id != 0, "Question does not exist");
        require(!questions[questionId].isResolved, "Question is resolved");
        require(bytes(content).length > 0, "Answer cannot be empty");
        require(
            block.timestamp - lastAnswerTime[msg.sender] >= MIN_ANSWER_INTERVAL,
            "Too soon to post another answer"
        );
        require(
            dailyAnswers[msg.sender] < MAX_DAILY_ANSWERS,
            "Daily answer limit reached"
        );

        answerCounter++;
        uint256 answerId = answerCounter;

        answers[answerId] = Answer({
            id: answerId,
            questionId: questionId,
            author: msg.sender,
            content: content,
            timestamp: block.timestamp,
            upvotes: 0,
            downvotes: 0,
            isApproved: false
        });

        // Update question
        questions[questionId].totalAnswers++;

        // Update user profile
        userProfiles[msg.sender].answersGiven++;
        userProfiles[msg.sender].lastActivity = block.timestamp;
        userAnswers[msg.sender].push(answerId);

        // Update anti-farming tracking
        lastAnswerTime[msg.sender] = block.timestamp;
        dailyAnswers[msg.sender]++;

        emit AnswerPosted(answerId, questionId, msg.sender, content);
    }

    /**
     * @dev Upvote an answer
     * @param answerId ID of the answer
     */
    function upvoteAnswer(uint256 answerId) external {
        require(answers[answerId].id != 0, "Answer does not exist");
        require(!answerUpvoters[answerId][msg.sender], "Already upvoted");
        require(answers[answerId].author != msg.sender, "Cannot upvote own answer");

        answers[answerId].upvotes++;
        answerUpvoters[answerId][msg.sender] = true;

        // Update question total upvotes
        questions[answers[answerId].questionId].totalUpvotes++;

        // Update author reputation
        userProfiles[answers[answerId].author].totalUpvotes++;
        userProfiles[answers[answerId].author].reputation += 5;

        emit AnswerUpvoted(answerId, msg.sender, answers[answerId].author);
        emit ReputationUpdated(
            answers[answerId].author,
            userProfiles[answers[answerId].author].reputation,
            5
        );
    }

    /**
     * @dev Approve the best answer (only by question asker)
     * @param questionId ID of the question
     * @param answerId ID of the answer to approve
     */
    function approveAnswer(uint256 questionId, uint256 answerId) external {
        require(questions[questionId].asker == msg.sender, "Only question asker can approve");
        require(questions[questionId].id != 0, "Question does not exist");
        require(answers[answerId].questionId == questionId, "Answer doesn't belong to question");
        require(!questions[questionId].isResolved, "Question already resolved");

        questions[questionId].isResolved = true;
        questions[questionId].approvedAnswer = answers[answerId].author;
        answers[answerId].isApproved = true;

        // Award reputation to approved answer author
        userProfiles[answers[answerId].author].reputation += 20;

        emit AnswerApproved(questionId, answerId, msg.sender);
        emit ReputationUpdated(
            answers[answerId].author,
            userProfiles[answers[answerId].author].reputation,
            20
        );
    }

    /**
     * @dev Get question details
     * @param questionId ID of the question
     */
    function getQuestion(uint256 questionId) external view returns (
        uint256 id,
        address asker,
        string memory title,
        string memory content,
        string[] memory tags,
        uint256 timestamp,
        uint256 bounty,
        bool isResolved,
        address approvedAnswer,
        uint256 totalAnswers,
        uint256 totalUpvotes
    ) {
        Question storage q = questions[questionId];
        return (
            q.id,
            q.asker,
            q.title,
            q.content,
            q.tags,
            q.timestamp,
            q.bounty,
            q.isResolved,
            q.approvedAnswer,
            q.totalAnswers,
            q.totalUpvotes
        );
    }

    /**
     * @dev Get answer details
     * @param answerId ID of the answer
     */
    function getAnswer(uint256 answerId) external view returns (
        uint256 id,
        uint256 questionId,
        address author,
        string memory content,
        uint256 timestamp,
        uint256 upvotes,
        uint256 downvotes,
        bool isApproved
    ) {
        Answer storage a = answers[answerId];
        return (
            a.id,
            a.questionId,
            a.author,
            a.content,
            a.timestamp,
            a.upvotes,
            a.downvotes,
            a.isApproved
        );
    }

    /**
     * @dev Get user profile
     * @param user User address
     */
    function getUserProfile(address user) external view returns (
        uint256 questionsAsked,
        uint256 answersGiven,
        uint256 totalUpvotes,
        uint256 reputation,
        bool isVerified,
        uint256 lastActivity
    ) {
        UserProfile storage profile = userProfiles[user];
        return (
            profile.questionsAsked,
            profile.answersGiven,
            profile.totalUpvotes,
            profile.reputation,
            profile.isVerified,
            profile.lastActivity
        );
    }

    /**
     * @dev Get questions by tag
     * @param tag Tag to search for
     */
    function getQuestionsByTag(string memory tag) external view returns (uint256[] memory) {
        return questionsByTag[tag];
    }

    /**
     * @dev Get user's questions
     * @param user User address
     */
    function getUserQuestions(address user) external view returns (uint256[] memory) {
        return userQuestions[user];
    }

    /**
     * @dev Get user's answers
     * @param user User address
     */
    function getUserAnswers(address user) external view returns (uint256[] memory) {
        return userAnswers[user];
    }

    /**
     * @dev Verify a user (only owner)
     * @param user User to verify
     */
    function verifyUser(address user) external onlyOwner {
        userProfiles[user].isVerified = true;
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}

contracts/RewardSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./QuestionManager.sol";

/**
 * @title RewardSystem
 * @dev Handles B3TR token rewards distribution based on user engagement and quality
 * @notice Integrates with VeBetter DAO for X-to-Earn rewards
 */
contract RewardSystem is Ownable, ReentrancyGuard {

    // Interfaces
    QuestionManager public questionManager;

    // Reward structures
    struct RewardPool {
        uint256 totalAllocated; // Total B3TR allocated to this pool
        uint256 distributed; // Amount already distributed
        uint256 startTime;
        uint256 endTime;
        bool isActive;
    }

    struct UserReward {
        address user;
        uint256 pendingRewards;
        uint256 totalEarned;
        uint256 lastClaimTime;
        uint256 reputationMultiplier;
    }

    // State variables
    mapping(uint256 => RewardPool) public rewardPools;
    mapping(address => UserReward) public userRewards;
    mapping(address => uint256) public userEngagementScore;

    uint256 public currentPoolId;
    uint256 public constant REWARD_CYCLE_DURATION = 7 days;
    uint256 public constant MIN_REPUTATION_FOR_REWARDS = 50;

    // Reward multipliers
    uint256 public constant APPROVED_ANSWER_MULTIPLIER = 300; // 3x for approved answers
    uint256 public constant HIGH_ENGAGEMENT_MULTIPLIER = 200; // 2x for high engagement
    uint256 public constant VERIFIED_USER_MULTIPLIER = 150; // 1.5x for verified users
    uint256 public constant BASE_REWARD = 100; // Base reward units

    // Events
    event RewardPoolCreated(
        uint256 indexed poolId,
        uint256 totalAllocated,
        uint256 startTime,
        uint256 endTime
    );

    event RewardsDistributed(
        address indexed user,
        uint256 amount,
        uint256 poolId,
        string reason
    );

    event RewardsClaimed(
        address indexed user,
        uint256 amount,
        uint256 timestamp
    );

    event EngagementScoreUpdated(
        address indexed user,
        uint256 newScore,
        uint256 change
    );

    constructor(address _questionManager) Ownable() {
        questionManager = QuestionManager(_questionManager);
    }

    /**
     * @dev Create a new reward pool (called by VeBetter DAO)
     * @param totalAllocated Total B3TR tokens allocated for this pool
     */
    function createRewardPool(uint256 totalAllocated) external onlyOwner {
        currentPoolId++;
        uint256 startTime = block.timestamp;
        uint256 endTime = startTime + REWARD_CYCLE_DURATION;

        rewardPools[currentPoolId] = RewardPool({
            totalAllocated: totalAllocated,
            distributed: 0,
            startTime: startTime,
            endTime: endTime,
            isActive: true
        });

        emit RewardPoolCreated(currentPoolId, totalAllocated, startTime, endTime);
    }

    /**
     * @dev Calculate and distribute rewards for a user's activity
     * @param user User address
     * @param activityType Type of activity (1: approved answer, 2: high engagement, 3: regular activity)
     * @param engagementScore User's engagement score
     */
    function distributeRewards(
        address user,
        uint256 activityType,
        uint256 engagementScore
    ) external {
        require(
            msg.sender == address(questionManager) || msg.sender == owner(),
            "Only question manager or owner can distribute rewards"
        );

        RewardPool storage pool = rewardPools[currentPoolId];
        require(pool.isActive, "No active reward pool");
        require(block.timestamp <= pool.endTime, "Reward pool expired");

        // Check if user has minimum reputation
        (, , , uint256 reputation, , ) = questionManager.getUserProfile(user);
        require(reputation >= MIN_REPUTATION_FOR_REWARDS, "Insufficient reputation for rewards");

        uint256 baseReward = BASE_REWARD;
        uint256 multiplier = 100; // Base multiplier

        // Apply multipliers based on activity type
        if (activityType == 1) {
            multiplier = APPROVED_ANSWER_MULTIPLIER;
        } else if (activityType == 2) {
            multiplier = HIGH_ENGAGEMENT_MULTIPLIER;
        }

        // Apply verified user multiplier
        (, , , , bool isVerified, ) = questionManager.getUserProfile(user);
        if (isVerified) {
            multiplier = (multiplier * VERIFIED_USER_MULTIPLIER) / 100;
        }

        // Calculate final reward
        uint256 finalReward = (baseReward * multiplier * engagementScore) / 10000;

        // Ensure we don't exceed pool allocation
        require(pool.distributed + finalReward <= pool.totalAllocated, "Reward pool exhausted");

        // Update user rewards
        userRewards[user].pendingRewards += finalReward;
        userRewards[user].totalEarned += finalReward;
        userRewards[user].reputationMultiplier = multiplier;

        // Update pool
        pool.distributed += finalReward;

        // Update engagement score
        userEngagementScore[user] += engagementScore;

        emit RewardsDistributed(user, finalReward, currentPoolId, "Activity reward");
        emit EngagementScoreUpdated(user, userEngagementScore[user], engagementScore);
    }

    /**
     * @dev Claim pending rewards
     */
    function claimRewards() external nonReentrant {
        UserReward storage userReward = userRewards[msg.sender];
        require(userReward.pendingRewards > 0, "No pending rewards");

        uint256 claimAmount = userReward.pendingRewards;
        userReward.pendingRewards = 0;
        userReward.lastClaimTime = block.timestamp;

        // In a real implementation, this would transfer B3TR tokens
        // For now, we'll just emit the event
        emit RewardsClaimed(msg.sender, claimAmount, block.timestamp);
    }

    /**
     * @dev Get user's reward information
     * @param user User address
     */
    function getUserRewardInfo(address user) external view returns (
        uint256 pendingRewards,
        uint256 totalEarned,
        uint256 lastClaimTime,
        uint256 engagementScore,
        uint256 reputationMultiplier
    ) {
        UserReward storage userReward = userRewards[user];
        return (
            userReward.pendingRewards,
            userReward.totalEarned,
            userReward.lastClaimTime,
            userEngagementScore[user],
            userReward.reputationMultiplier
        );
    }

    /**
     * @dev Get current reward pool information
     */
    function getCurrentRewardPool() external view returns (
        uint256 poolId,
        uint256 totalAllocated,
        uint256 distributed,
        uint256 remaining,
        uint256 startTime,
        uint256 endTime,
        bool isActive
    ) {
        RewardPool storage pool = rewardPools[currentPoolId];
        return (
            currentPoolId,
            pool.totalAllocated,
            pool.distributed,
            pool.totalAllocated - pool.distributed,
            pool.startTime,
            pool.endTime,
            pool.isActive
        );
    }

    /**
     * @dev Calculate engagement score for a user
     * @param user User address
     */
    function calculateEngagementScore(address user) external view returns (uint256) {
        (, , uint256 totalUpvotes, uint256 reputation, , ) = questionManager.getUserProfile(user);

        // Engagement score based on upvotes and reputation
        uint256 score = (totalUpvotes * 10) + (reputation * 5);

        // Bonus for recent activity
        (, , , , , uint256 lastActivity) = questionManager.getUserProfile(user);
        if (block.timestamp - lastActivity < 1 days) {
            score += 50; // Recent activity bonus
        }

        return score;
    }

    /**
     * @dev Get leaderboard of top users by engagement
     * @param limit Number of top users to return
     */
    function getTopUsers(uint256 limit) external view returns (
        address[] memory users,
        uint256[] memory scores,
        uint256[] memory rewards
    ) {
        // This is a simplified implementation
        // In a real scenario, you'd need to maintain a sorted list
        // or use an off-chain service for complex queries

        address[] memory topUsers = new address[](limit);
        uint256[] memory topScores = new uint256[](limit);
        uint256[] memory topRewards = new uint256[](limit);

        // For now, return empty arrays
        // In production, implement proper sorting algorithm
        return (topUsers, topScores, topRewards);
    }

    /**
     * @dev Update question manager address (only owner)
     * @param newQuestionManager New question manager address
     */
    function updateQuestionManager(address newQuestionManager) external onlyOwner {
        questionManager = QuestionManager(newQuestionManager);
    }

    /**
     * @dev Emergency pause (only owner)
     */
    function pauseRewards() external onlyOwner {
        rewardPools[currentPoolId].isActive = false;
    }

    /**
     * @dev Resume rewards (only owner)
     */
    function resumeRewards() external onlyOwner {
        rewardPools[currentPoolId].isActive = true;
    }
}

contracts/VeChainQuora.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./QuestionManager.sol";
import "./RewardSystem.sol";

/**
 * @title VeChainQuora
 * @dev Main contract that integrates QuestionManager and RewardSystem
 * @notice This is the main entry point for the VeChain Q&A platform
 */
contract VeChainQuora {

    // Contract instances
    QuestionManager public questionManager;
    RewardSystem public rewardSystem;

    // Platform statistics
    uint256 public totalUsers;
    uint256 public totalQuestions;
    uint256 public totalAnswers;
    uint256 public totalRewardsDistributed;

    // Platform settings
    address public owner;
    bool public isActive;

    // Events
    event PlatformInitialized(
        address indexed questionManager,
        address indexed rewardSystem,
        address indexed owner
    );

    event UserRegistered(
        address indexed user,
        uint256 timestamp
    );

    event PlatformStatsUpdated(
        uint256 totalUsers,
        uint256 totalQuestions,
        uint256 totalAnswers,
        uint256 totalRewards
    );

    constructor() {
        owner = msg.sender;
        isActive = true;

        // Deploy QuestionManager
        questionManager = new QuestionManager();

        // Deploy RewardSystem with QuestionManager address
        rewardSystem = new RewardSystem(address(questionManager));

        emit PlatformInitialized(
            address(questionManager),
            address(rewardSystem),
            owner
        );
    }

    /**
     * @dev Register a new user on the platform
     */
    function registerUser() external {
        require(isActive, "Platform is not active");

        // In a real implementation, you might want to check if user is already registered
        // For now, we'll just emit the event
        emit UserRegistered(msg.sender, block.timestamp);
    }

    /**
     * @dev Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 users,
        uint256 questions,
        uint256 answers,
        uint256 rewards
    ) {
        return (
            totalUsers,
            totalQuestions,
            totalAnswers,
            totalRewardsDistributed
        );
    }

    /**
     * @dev Get contract addresses
     */
    function getContractAddresses() external view returns (
        address questionManagerAddr,
        address rewardSystemAddr
    ) {
        return (
            address(questionManager),
            address(rewardSystem)
        );
    }

    /**
     * @dev Toggle platform activity (only owner)
     */
    function togglePlatform() external {
        require(msg.sender == owner, "Only owner can toggle platform");
        isActive = !isActive;
    }

    /**
     * @dev Update platform statistics (only contracts)
     */
    function updateStats(
        uint256 users,
        uint256 questions,
        uint256 answers,
        uint256 rewards
    ) external {
        require(
            msg.sender == address(questionManager) || 
            msg.sender == address(rewardSystem),
            "Only authorized contracts can update stats"
        );

        totalUsers = users;
        totalQuestions = questions;
        totalAnswers = answers;
        totalRewardsDistributed = rewards;

        emit PlatformStatsUpdated(users, questions, answers, rewards);
    }
}

